using System;
using System.IO;

namespace CsharpExecutor
{
    public static class Constants
    {
        public static void exe2string()
        {
            byte[] buffer =
                System.IO.File.ReadAllBytes("C:\\Users\\echo0d\\Desktop\\CsharpExecutor\\CsharpExecutor\\testcalc.exe");
            string base64Str = Convert.ToBase64String(buffer);
            File.WriteAllText("testcalc.txt", base64Str);
        }

        // 这里模拟把一个exe文件保存为base64编码后的字符串常量
        // 这个常量读到内存，就可以实现在内存中执行某个exe文件，而不需要保存在终端上
        public const string Base64Exe =
            "";
    }
}