# base64编码的字符串
$base64 = "";
$bins  = [System.Convert]::FromBase64String($base64);
$invoke = [System.Reflection.Assembly]::Load($bins);
[System.Console]::WriteLine($invoke);
$invoke.EntryPoint.Invoke($null,$null)

